-- FAQ
CREATE TABLE FAQ (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    question TEXT,
    answer TEXT,
    createDate DATE
);


-- CAR
CREATE TABLE Car (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    make TEXT,
    model TEXT,
    year INTEGER,
    fuelType TEXT,
    kilometers INTEGER,
    details TEXT,
    price INTEGER,
    photoUrl TEXT
);


